  let navContainer = document.querySelector('.nav-list');
  let sectionContainer = document.querySelector('.dictionary');
  let popupContainer = document.querySelector('.modal');
  
  let createNavItemString = ({key, items}) => {
    return `
    <li class="nav-list-item">
       <a class="nav-list-link" ${items.length > 0 ? `href="#${key}"` : ''} >${key}</a>
    </li>
  `
  }
  
  let createSectionString = ({key, items}) => {
    return ` 
    <section class="dictionary-section"> 
       <h2 class="letter-key" id="${key}">${key}</h2>
       <ul class="term-list">
          ${items.map(({term}) => `
             <li class="term-item">
                <a class="term-link" href="#" data-value="${term}">${term}</a>
             </li>
          `).join('')}
       </ul>
    </section>
  `;
  }
  
  let createPopupString = ({description, cite}) => {
    return `
    <div class="modal-popup">
       <button class="close-button" type="button">
          <span class="visually-hidden">Закрыть окно</span>
       </button>
       <div class="modal-content">
          ${description}
          <cite>${cite}</cite>
       </div>
    </div>
    ` 
  }
  
  navContainer.addEventListener('click', function(evt) {
    const element = evt.target.closest('a');
  
    if (!element) {
      return
    }
    evt.preventDefault();
  
    const blockId = element.getAttribute('href');
    const block = document.querySelector(blockId);
  
    if (block) {
      block.scrollIntoView({
      behavior: 'smooth',
      })
    }
    
  })
  
  sectionContainer.addEventListener('click', function(evt) {
    const element = evt.target.closest('a');
  
    if (!element) {
      return
    }
    evt.preventDefault();
  
    const value = element.dataset.value;
  
    const termsGroup = terms.find(({key}) => value.indexOf(key) === 0);
   
    if (termsGroup) {
      const currentTerm = termsGroup.items.find((item) => item.term === value);
  
      if (currentTerm) {
        const popupString = createPopupString(currentTerm);
        popupContainer.innerHTML = '';
        popupContainer.insertAdjacentHTML('beforeend', popupString);
        popupContainer.classList.add('shown')
      }
    }
  
    let closeButton = popupContainer.querySelector('.close-button');
    closeButton.onclick = function() {
      popupContainer.classList.remove('shown');
    }
  })
  
  let render = function() {
    navContainer.innerHTML = '';
    let navElementString = terms.map(createNavItemString).join('');
    navContainer.insertAdjacentHTML('beforeend', navElementString);
  
    sectionContainer.innerHTML = '';
    let sectionElementString = terms.map(createSectionString).join('');
    sectionContainer.insertAdjacentHTML('beforeend', sectionElementString);
  
  }
  
  render();